import { useEffect, useState } from "react";
import "../App.css";

function MyAppointment() {
  const [appointments, setAppointments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/getAppointments");

        if (!res.ok) {
          throw new Error("Failed to fetch appointments");
        }

        const data = await res.json();
        setAppointments(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchAppointments();
  }, []);

  if (loading) {
    return <h3 className="text-center mt-5">Loading appointments...</h3>;
  }

  if (error) {
    return <h3 className="text-center text-danger mt-5">{error}</h3>;
  }

  return (
    <div className="container my-5">
      <h2 className="text-center text-primary mb-4">My Appointments</h2>

      {appointments.length === 0 ? (
        <p className="text-center">No appointments found</p>
      ) : (
        <div className="row">
          {appointments.map((item) => (
            <div className="col-md-6 mb-4" key={item._id}>
              <div className="card shadow-sm p-3">
                <h5 className="text-primary">{item.fullName}</h5>
                <p><strong>Email:</strong> {item.email}</p>
                <p><strong>Phone:</strong> {item.phone}</p>
                <p><strong>Doctor:</strong> {item.doctor}</p>
                <p><strong>Department:</strong> {item.department}</p>
                <p><strong>Date:</strong> {item.appointmentDate}</p>
                <p><strong>Time:</strong> {item.appointmentTime}</p>
                <p><strong>Visit Type:</strong> {item.visitType}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyAppointment;
