import { useState, useEffect } from "react";
import { getMyAppointments } from "../apiService"; // your API function

function MyAppointments() {
  const [appointments, setAppointments] = useState([]);

  useEffect(() => {
    const fetchAppointments = async () => {
      try {
        const data = await getMyAppointments();
        setAppointments(data.appointments || []); // ensure array
      } catch (error) {
        console.error(error);
      }
    };
    fetchAppointments();
  }, []);

  return (
    <section className="container my-5">
      <h2 className="text-center mb-4">My Appointments</h2>
      {appointments.length === 0 ? (
        <p>No appointments booked yet.</p>
      ) : (
        <div className="row">
          {appointments.map((app) => (
            <div key={app.id} className="col-md-6 mb-3">
              <div className="card p-3 shadow-sm">
                <h5>{app.fullName}</h5>
                <p>
                  <strong>Email:</strong> {app.email} <br />
                  <strong>Phone:</strong> {app.phone} <br />
                  <strong>Department:</strong> {app.department} <br />
                  <strong>Doctor:</strong> {app.doctor} <br />
                  <strong>Date:</strong> {app.appointmentDate} <br />
                  <strong>Time:</strong> {app.appointmentTime} <br />
                  <strong>Visit Type:</strong> {app.visitType} <br />
                  <strong>Reason:</strong> {app.reasonForVisit} <br />
                  <strong>Symptoms:</strong> {app.symptoms || "N/A"}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </section>
  );
}

export default MyAppointments;
