const BASE_URL = "http://localhost:5000/api";

/* ================= AUTH APIs ================= */
export const signupUser = async (userData) => {
  const res = await fetch(`${BASE_URL}/auth/register`, { // endpoint should match backend /register
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(userData), // fixed syntax: removed "bod if its wrong thts solvey"
  });
  return res.json();
};

export const loginUser = async (loginData) => {
  const res = await fetch(`${BASE_URL}/auth/login`, {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify(loginData),
  });
  return res.json();
};

/* ================= APPOINTMENT APIs ================= */
export const bookAppointment = async (appointmentData) => {
  const res = await fetch(`${BASE_URL}/App/book`, {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
    body: JSON.stringify(appointmentData),
  });
  return res.json();
};

export const getMyAppointments = async () => {
  const res = await fetch(`${BASE_URL}/appointments/myappointment`, { // endpoint should be /my
    headers: {
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  });
  return res.json();
};

/* ================= USER APIs ================= */
export const logoutUser = () => {
  localStorage.removeItem("token");
};

export const getUserProfile = async () => {
  const res = await fetch(`${BASE_URL}/auth/profile`, {
    headers: {  
      Authorization: `Bearer ${localStorage.getItem("token")}`,
    },
  });
  return res.json();
};
